export function GetUniqueWords(e,r=20,t=null){null!==t&&(e=e.filter((e=>!t.includes(e.value))));let n=[...e];for(let e=n.length-1;e>0;e--){let r=Math.floor(Math.random()*(e+1));[n[e],n[r]]=[n[r],n[e]]}return n.slice(0,r)}export function GetWordIndex(e){return Nouns.concat(Verbs).findIndex((r=>r===e))}export function GetJustWords(e){return e.map((e=>e.value)).join(",")}export function createWordArray(e,r=0){return e.map(((e,t)=>`{"id":${r+t},"value":"${e}"}`)).join(",")}export function sortWords(e){return e.sort(((e,r)=>e.value.localeCompare(r.value)))}export function findDuplicateWords(e){return e.filter(((r,t)=>{let n=JSON.stringify(r);return e.map((e=>JSON.stringify(e))).indexOf(n)!==t&&e.map((e=>JSON.stringify(e))).lastIndexOf(n)===t}))}